mapper.select:
SELECT stu_id AS stuId, school_number AS schoolNumber, id_number AS idNumber FROM stu_base_info
1 = 1 AND is_deleted = 0
AND id_number IN
#{key}
AND school_number IN
#{key}

select bi.*, academy.`name` academyName, major.`name` majorName, grade.`name` gradeName, classinfo.`name` className
, (case bi.current_status WHEN 0 THEN '在校' WHEN 1 THEN '实习' WHEN 2 THEN '就业' WHEN 3 THEN '请假' WHEN 4 THEN '休学' WHEN 5 THEN '退学' WHEN 6 THEN '毕业' ElSE '' END ) currentStatusName, (case bi.residence_type WHEN 0 THEN '非农户口' WHEN 1 THEN '农业户口' ElSE '' END ) residenceTypeName, (case bi.school_roll_status WHEN 0 THEN '在册在籍' WHEN 1 THEN '在册不在籍' WHEN 2 THEN '在籍退学' WHEN 3 THEN '非在册非在籍' WHEN 4 THEN '提前入学' ElSE '' END ) schoolRollStatusName
from stu_base_info bi left join sys_dept academy on bi.academy_id = academy.dept_id left join sys_dept major on bi.major_id = major.dept_id left join sys_dept grade on bi.grade_id = grade.dept_id left join sys_dept classinfo on bi.class_id = classinfo.dept_id where ( 1 = 1 and bi.is_deleted = 0)
AND bi.stu_name like concat('%',#{key.stuName},'%')
AND bi.id_number like concat('%',#{key.idNumber},'%')
AND bi.head_teacher like concat('%',#{key.headTeacher},'%')
and bi.academy_id = #{academyId}
and stu_id = #{stuId}
and ( bi.academy_id = #{deptId} or bi.major_id = #{deptId} or bi.grade_id = #{deptId} or bi.class_id = #{deptId} )

select bi.*
from stu_base_info bi
where
  bi.current_school_year  <  bi.schooling_length

  and bi.academy_id = #{academyId}


  and bi.academy_id = #{deptId}


  and bi.major_id = #{deptId}


  and bi.grade_id = #{deptId}


  and bi.class_id = #{deptId}

and bi.is_deleted = 0
and bi.current_status in (0,1,3)
and entrance_status in (300,400)
